import { Slideshow } from '@material-ui/icons';
import React from 'react'
import styled , {css} from 'styled-components'
import { Button } from './Button'


const BannerSection = styled.section`

    height: 100vh;
    max-height: 1100px;
    position: relative;
    overflow: hidden;

`;
const BannerWrapper = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    position: relative;

`;

const BannerContent = styled.div`

    position: relative;
    z-index: 10;
    display: flex;
    flex-direction: column;
    max-width: 1600px;
    width: calc(100% - 100px);

`;
const BannerImage = styled.img`
    position: absolute;
    top:0;
    left:0;
    width: 100vw;
    height: 100vh;
    object-fit: cover;
`;

const BannerSlide = styled.div`
    z-index: 1;
    width: 100%;
    height: 100%;

`;
const BannerSlider = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;


    &::before{
        content: '';
        position: absolute;
        z-index: 2;
        width: 100%;
        height: 100vh;
        bottom: 0vh;
        left: 0;
        overflow: hidden;
        opacity: 0.4;
    }


`;
const Banner = ({slides}) => {
    return (

        <BannerSection>

            <BannerWrapper>
                {slides.map((slide, index) => {
                    return (
                        <BannerSlide key={index}>
                        <BannerSlider>
                            <BannerImage />
                            <BannerContent>
                                <h1>{slide.title}</h1>
                                <p>{slide.price}</p>
                                <Button to={slide.path} primary='true' css={`max-width: 160px;`}>{slide.label}</Button>
                            </BannerContent>
                        </BannerSlider>
                    </BannerSlide>   
                    )
                })}


            </BannerWrapper>


        </BannerSection>
    );
};

export default Banner
