import React ,{useState} from 'react'
import styled from 'styled-components'
import { Button } from './Button'



const arrowButton = css`
    width: 50px;
    height: 50px;

`;
const BannerSection = styled.section`
    height: 100vh;
    max-height: 1100px;
    position: relative;
    overflow: hidden;

`;
const BannerWrapper = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    position: relative;

`;

const BannerContent = styled.div`

   position: relative;
    z-index: 1;
    display: flex;
    flex-direction: column;
    max-width: 1600px;
    width: calc(100% - 100px); 
    color: #fff;


    h1{
        font-size: clamp(1rem,8vw,2rem);
        font-weight: 400;
        text-transform: uppercase;
        text-shadow: 0px 0px 20px  rgba(0,0,0,0.4);
        text-align: left;
        margin-bottom: 0.8rem;
    }
    p{
        margin-bottom: 1.2rem;
        text-shadow: 0px 0px 20px rgba(0,0,0,0.4) ;
    }

`;
const BannerImage = styled.img`
    position: absolute;
    top:0;
    left:0;
    width: 100vw;
    height: 100vh;
    object-fit: cover;
`;

const BannerSlide = styled.div`
    z-index: 1;
    width: 100%;
    height: 100%;

`;
const BannerSlider = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;


    &::before{
        content: '';
        position: absolute;
        z-index: 2;
        width: 100%;
        height: 100vh;
        bottom: 0vh;
        left: 0;
        overflow: hidden;
        opacity: 0.4;
    }


`;


const PrevArrow = styled(IoArrowBack)`


`;

const NextArrow = styled(IoArrowForward)`

`;
const Banner = ({ slides }) => {

const [current , setCurrent] = useState(0);
    return (

        <BannerSection>

            <BannerWrapper>
                {slides.map((slide, index) => {
                    return (
                        <BannerSlide key={index}>
                            <BannerSlider>
                                <BannerImage src={slide.image} alt={slide.alt} />
                                <BannerContent>
                                    <h1>{slide.title}</h1>
                                    <p>{slide.price}</p>
                                    <Button to={slide.path} primary='true' css={`max-width: 160px;`}>{slide.label}</Button>
                                </BannerContent>
                            </BannerSlider>
                        </BannerSlide>
                    )
                })}


            </BannerWrapper>


        </BannerSection>
    );
};

export default Banner
