import React, { useState } from 'react'
import styled, { css } from 'styled-components'
import { Button } from './Button'
import { FaArrowCircleLeft, FaArrowCircleRight} from 'react-icons/fa'


const arrowButton = css`
    height: 50px;
    color: #fff;
    cursor: pointer;
    background-color: #6B705C;
    border-radius: 50px;
    padding: 10px ;
    margin-right: 1rem;
    user-select: none;
    transition: 0.3s;


    &:hover{
        background: #A5A58D;
        transform: scale(1.05);
    }
`;
const BannerSection = styled.section`
    height: 100vh;
    max-height: 1100px;
    position: relative;
    overflow: hidden;

`;
const BannerWrapper = styled.div`
    width: 100%;
    height: 100%;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    position: relative;

`;

const BannerContent = styled.div`

   position: relative;
    z-index: 1;
    display: flex;
    flex-direction: column;
    max-width: 1600px;
    width: calc(100% - 100px); 
    color: #fff;


    h1{
        font-size: clamp(1rem,8vw,2rem);
        font-weight: 400;
        text-transform: uppercase;
        text-shadow: 0px 0px 20px  rgba(0,0,0,0.4);
        text-align: left;
        margin-bottom: 0.8rem;
    }
    p{
        margin-bottom: 1.2rem;
        text-shadow: 0px 0px 20px rgba(0,0,0,0.4) ;
    }

`;
const BannerImage = styled.img`
    position: absolute;
    top:0;
    left:0;
    width: 100vw;
    height: 100vh;
    object-fit: cover;
`;

const BannerSlide = styled.div`
    z-index: 1;
    width: 100%;
    height: 100%;

`;
const BannerSlider = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    display: flex;
    align-items: center;
    justify-content: center;


    &::before{
        content: '';
        position: absolute;
        z-index: 2;
        width: 100%;
        height: 100vh;
        bottom: 0vh;
        left: 0;
        overflow: hidden;
        opacity: 0.4;
    }


`;


const PrevArrow = styled(FaArrowCircleLeft)`
        ${arrowButton}

`;

const NextArrow = styled(FaArrowCircleRight)`
    ${arrowButton}

`;

const SliderButtons = styled.div`
    /* position: absolute; */
    bottom: 50px;
    right: 50px;
    display: flex;
    z-index: 10;


`;
const Banner = ({ slides }) => {

    const [current, setCurrent] = useState(0);
    const length = slides.length

    const nextSlide = () => {
        setCurrent(current === length - 1 ? 0 : current + 1);
    }

    const prevSlide = () => {
        setCurrent(current === 0 ? length - 1 : current - 1);
    }

    return (

        <BannerSection>

            <BannerWrapper>
                {slides.map((slide, index) => {
                    return (
                        <BannerSlide key={index}>
                            {index === current && (
                                <BannerSlider>
                                    <BannerImage src={slide.image} alt={slide.alt} />
                                    <BannerContent>
                                        <h1>{slide.title}</h1>
                                        <p>{slide.price}</p>
                                        {/* <Button primary='true' css={`max-width: 160px;`}>{slide.label}</Button> */}
                                    </BannerContent>
                                </BannerSlider>

                            )}

                        </BannerSlide>
                    )
                })}
                <SliderButtons>
                    <PrevArrow onClick={prevSlide} />
                    <NextArrow onClick={nextSlide} />
                </SliderButtons>

            </BannerWrapper>


        </BannerSection>
    );
};

export default Banner
