import React from 'react'
import styled from 'styled-components'



const CartSection = styled.div`
  margin: 10px;
    border-radius: 10px;
    overflow: hidden;
    box-shadow: 0px 6px 18px -9px rgba(0, 0, 0, 0.75);
    transition: transform 100ms ease-in;
    cursor: pointer;

    &:hover {
    transform: scale(1.07);
    }
`;
const CartWrapper = styled.div`
    display: flex;
    margin-top: -9px;
      border-radius: 10px;
      padding: 20px;
      padding-top: 20px;
      border: 1;
`;
const CartContainer = styled.div`



      h2{
        font-size: 18px;
      font-weight: 600;
      }
      p{
        font-size: 14px;
      font-weight: 300;
      margin-top: 8px;
      margin-bottom: 8px;
      }
`;
const CartImage = styled.img`

object-fit: fill;
    min-width: 300px;
    min-height: 200px;
    width: 100%;
`;
const CartContent = styled.div``;



const Cart = ({ carts }) => {
    return (
        <CartSection>
            <CartWrapper>
                {carts.map((cart, index) => {
                    return (
                        <CartContainer key={index}>
                            <Content>
                                <CartImage src={cart.image} alt={cart.alt} />
                                <CartContent>
                                    <h2>{cart.title}</h2>
                                    <p>{cart.price}</p>
                                </CartContent>
                            </Content>





                        </CartContainer>
                    )
                })}
            </CartWrapper>
        </CartSection>

    )
}

export default Cart
