import React, { useState } from 'react';
import styled from 'styled-components';
import { FaSearch } from 'react-icons/fa';
import { useHistory } from 'react-router-dom/cjs/react-router-dom.min';
import { style } from '@mui/system';


const Section = styled.section`
    justify-content:center;
    display: flex;
    align-items: center;
    position: sticky;
    top: 0;
    width: 100%;




`;
const Container = styled.div``;
const InputSearch = styled.div`

    display: flex;
    flex: 1;
    align-items: center;
    max-width: fit-content;
    padding: 20px 50px ;
    height: 80px;
    border: 1px solid lightgray;
    border-radius: 80px;
    //box-shadow: 5px 5px 5px 5px gray;
    margin-top: 100px;
`;

// const Select = styled.select`
//     padding: 5px;
//     border: none;
//     outline: none;
//     width: 80%;
//     margin-bottom: 3px;
//     background-color: #fff;
//     font-family: 'Lora' , sans-serif;
// `;
const Input = styled.input.attrs({ type: "text" })`
    border: 1px solid #fff;
    outline: none;
    padding: 8px;
    &:focus{
        outline: none;
        box-shadow: none;
        border: 1px solid var(--color-secondary);
    }

`;

const ShowSearch = styled.div``;
const Button = styled.button``;


const SearchSection = () => {
    const history = useHistory();
    const [showSearch, setShowSearch] = useState(false);
    return (
        <Section>
            <Container>
                <InputSearch>
                    <Input type='text' />

                    <FaSearch />

                </InputSearch>
                <ShowSearch>
                    {showSearch && <h1>Search</h1>}
                    <Button onClick={() => setShowSearch(!showSearch)} variant="outlined">{showSearch ? "Hide": "Search Dates"}</Button>
                </ShowSearch>
                <SearchInfo>
                    <h1>Get out and stretch out your  imagination </h1>
                    <h5>Plan a different kind  of getaway to uncover the hidden gems near you </h5>
                    <Button onClick={() => history.push('/search')} variant="outlined">Explore</Button>
                </SearchInfo>

            </Container>
        </Section>

    )
}

export default SearchSection
